from django.db import models
#from django.utils import timezone


class User(models.Model):
	# Id = auto generated by django

	username = models.CharField(max_length=30, primary_key=True)
	password = models.CharField(max_length=30)
	created_date = models.CharField(max_length=30)

class student_info(models.Model):
	lastname = models.CharField(max_length=30, primary_key=True)
	firstname = models.CharField(max_length=30)
	middlename = models.CharField(max_length=30)
	gender = models.CharField(max_length=6)
	birthplace = models.CharField(max_length=30)
	civil_status  = models.CharField(max_length=10)
	religion = models.CharField(max_length=30)
	nationality = models.CharField(max_length=20)
	height = models.CharField(max_length=6)
	weight = models.CharField(max_length=6)
	email = models.CharField(max_length=30)
	mobile_Number = models.CharField(max_length=30)
	tel_Number = models.CharField(max_length=30)
	present_address = models.CharField(max_length=100)
	permanent_address = models.CharField(max_length=100)

class familyBackground_info(models.Model):
	student_info_id = models.TextField(primary_key=True)
	spouse_name = models.CharField(max_length=100)
	spouse_birthdate = models.CharField(max_length=20)
	spouse_educational = models.CharField(max_length=50)
	spouse_occupation = models.CharField(max_length=50)
	spouse_company = models.CharField(max_length=100)
	spouse_company_address = models.CharField(max_length=100)
	spouse_email = models.CharField(max_length=50)
	spouse_children = models.CharField(max_length=5000)
	father_name = models.CharField(max_length=100)
	father_birthdate = models.CharField(max_length=20)
	father_educational = models.CharField(max_length=50)
	father_occupation = models.CharField(max_length=50)
	father_company = models.CharField(max_length=100)
	father_company_address = models.CharField(max_length=100)
	father_email = models.CharField(max_length=50)
	mother_name = models.CharField(max_length=100)
	mother_name = models.CharField(max_length=20)
	mother_educational = models.CharField(max_length=50)
	mother_occupation = models.CharField(max_length=50)
	mother_company = models.CharField(max_length=100)
	mother_company_address = models.CharField(max_length=100)
	mother_email = models.CharField(max_length=50)
	guardian_name = models.CharField(max_length=100)
	guardian_relationship = models.CharField(max_length=20)
	guardian_address = models.CharField(max_length=100)
	guardian_occupation = models.CharField(max_length=50)
	guardian_company = models.CharField(max_length=100)
	guardian_company_address = models.CharField(max_length=100)
	guardian_mobile = models.CharField(max_length=15)
	guardian_email = models.CharField(max_length=50)

